import React,{ useState } from "react";
import {Link} from "react-router-dom";
import {
  MDBBtn,
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBCard,
  MDBCardBody,
  MDBCardImage,
  MDBInput,
  MDBIcon,
  MDBCheckbox,
} from "mdb-react-ui-kit";

function App() {
  
  const [details,setDetails]=useState({
    fname: "",
    email: "",
    number:"",
    go:false
  });
  
  
  function handle(data){
    const {id,value}=data.target
    setDetails((prev) => {
      return {
        ...prev,
        [id]: value,
      };
    });
    let s=details.email;
    
    let num=s.length;
    if(num<=10){setGo(false);}
    else if(details.fname.length>0 && details.number.length===10 && s.substring(num-10)==="@gmail.com"){
      setDetails(details.go)
    }
    else{
     
    }
  }
  
  return (
    <MDBContainer fluid>
      <MDBCard className="text-black m-5" style={{ borderRadius: "25px" }}>
        <MDBCardBody>
          <MDBRow>
            <MDBCol
              md="10"
              lg="6"
              className="order-2 order-lg-1 d-flex flex-column align-items-center"
            >
              <p className="text-center h1 fw-bold mb-5 mx-1 mx-md-4 mt-4">
                Contact details
              </p>

              <div className="d-flex flex-row align-items-center mb-4 ">
                <MDBIcon fas icon="user me-3" size="lg" />
                <MDBInput
                  label="First Name*"
                  id="fname"
                  type="text"
                  className="fname"
                  onChange={handle}
                />
              </div>
              <div className="d-flex flex-row align-items-center mb-4 ">
                <MDBIcon fas icon="user me-3" size="lg" />
                <MDBInput
                  label="Last Name"
                  id="lname"
                  type="text"
                  className="lname"
                />
              </div>
              <div className="d-flex flex-row align-items-center mb-4">
                <MDBIcon fas icon="envelope me-3" size="lg" />
                <MDBInput
                  label="Email*"
                  type="email"
                  id="email"
                  className="email"
                  onChange={handle}
                />
              </div>

              <div className="d-flex flex-row align-items-center mb-4 ">
                <MDBIcon fas icon="user me-3" size="lg" />
                <MDBInput
                  label="Mobile*"
                  id="number"
                  type="Number"
                  className="number"
                  onChange={handle}
                />
              </div>

              
              {go && (
               
                  <MDBBtn className="mb-4" size="lg" href="/home" >
                    Submit
                  </MDBBtn>
               
              )}
            </MDBCol>

            <MDBCol
              md="10"
              lg="6"
              className="order-1 order-lg-2 d-flex align-items-center"
            >
              <MDBCardImage
                src="https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-registration/draw1.webp"
                fluid
              />
            </MDBCol>
          </MDBRow>
        </MDBCardBody>
      </MDBCard>
    </MDBContainer>
  );
}

export default App;
