import React,{useEffect,useState} from "react";
import { Link } from "react-router-dom";
import AOS from "aos";
import "aos/dist/aos.css";

function Movie(props) {
   const [found, setNotFound] = useState(null);
   const [tmdata, setmdata] = useState([]);
   
   useEffect(() => {
     fetch(
       `https://api.themoviedb.org/3/search/movie?query=${props.title}&api_key=bee8ce9f0d5a33ee50837d31a61a64eb`
     )
       .then((res) => res.json())
       .then((data) => setmdata(data.results[0]))

       .catch((err) => {
         setNotFound(true);
       });
   }, []);

useEffect(() => {
  AOS.init({
    duration: 1000,
   
  });
  window.scrollTo(0, 0);
}, []);

  return (
    <>
      <figure className="image-block" data-os="fade-down">
        
        <img src={props.poster} />
        <figcaption>
          <p>{props.title}</p>
          <p>{props.genre}</p>
          <Link
            
            state={{ name: props.name, data: tmdata, title: props.title }}
            to={`${tmdata.id}`}
          >
            <button>Book Now</button>
          </Link>
        </figcaption>
      </figure>

      
    </>
  );
}

export default Movie;
