import React from "react";
import { useParams, Link, Outlet, useLocation } from "react-router-dom";

function Payment() {
  const location = useLocation();
  const cost = location.state.totalcost;
  const count = location.state.counts;
  const theater = location.state.theatre;
  const time = location.state.time;
  const date = location.state.date;
  const name = location.state.name;
  const title = location.state.title;
  const photo =location.state.photo;
  var num = cost / count;
  return (
    <>
      <div class="payment">
        <div className="pay-photo">
          <img
            className="pay-image"
            src={`https://image.tmdb.org/t/p/w500/` + photo}
            height="120"
            width="120"
          />
        </div>
        <div className="pay-content">
          <p>
            <strong>{title}</strong>
          </p>
          <p>
            {date}- {time}
          </p>
        </div>
      </div>
      <Link state={{ name: name }} to="/city">
        <button>lj</button>
      </Link>
      <div>
        <table>
          <tr>
            <td>Cost of each ticket</td>
            <td>{}</td>
          </tr>
          <tr>
            <td>No. of tickets</td>
          </tr>

          <tr>
            <td>Total Cost</td>
          </tr>
        </table>
      </div>
    </>
  );
}

export default Payment;
