import React, { useEffect,useState}  from "react";
import movies from "../movies"
import { useParams,Link } from "react-router-dom";
import Booking from "./Booking"


function Details(){
    const params=useParams()
    const userId=params.userId
    
    const obj=movies.find(function(num){
       
        return num.title===userId
    })
     const [movie,setmovieData]=useState([])
    const [found,setNotFound]=useState(null);

    function handleClick(data){
      console.log(data);
    }

useEffect(() => {
    fetch(`http://www.omdbapi.com/?t=${obj.title}&y=${obj.year}&apikey=961ea94b`)
      .then((res) => res.json()
      ).then(data => setmovieData(data))
      
      .catch((err) => {
        setNotFound(true);
      })
      
  }, [])
      console.log(movie.Plot);
    return (
        
        <>
        <div class="movie">
       

  <div className="poster">
  <img className="movie-image" src={movie.Poster} height="400" width="400" />
  </div>
    

<div className="matter">
    <h1 class="matter-title">{movie.Title} </h1>
   
    <div className="rating">
     <span className="imdb"><h2 class="abcd">{movie.imdbRating}/10</h2></span>
     <span> {movie.imdbVotes} </span>
     <span>votes</span>
    </div>
     <div className="language">

    
     </div>
    <div className="elements">
    <ul >
       <li>{movie.Runtime}</li>
       <li>{movie.Genre}</li>
      
    </ul>
    </div>

       <div className="movie-container">
         <label>Select a Theatre</label>
         <select  id="movie">
           <option value="220">PVR</option>
           <option value="320">PVP</option>
           <option value="250">SP</option>
           <option value="230">CELLULOID</option>
         </select>
       </div>

       <Link className="book-ticket" to='/booking'>
          <button type="button" id="ticket-button" className={movie.title} onClick={handleClick}> Boot tickets</button>
       </Link>
      

      </div>
</div>
  
<div className="plot">
<h2>About the Movie</h2>
<p> {movie.Plot}</p>
</div>
 
 
        </>
    )
}
export default Details;
