import React, { useState } from "react";
import ScrollCarousel from "scroll-carousel";

const [found, setNotFound] = useState(null);
const [review, setReview] = useState(null);
useEffect(() => {
  fetch(
    `https://api.themoviedb.org/3/movie/${mvedata.id}/reviews?api_key=bee8ce9f0d5a33ee50837d31a61a64eb`
  )
    .then((res) => res.json())
    .then((data) => setReview(data.results))

    .catch((err) => {
      setNotFound(true);
    });
}, []);
const generateReview = () => {
  return review?.map((data, i) => (
    <div className="reviewdata1">
      <div
        style={{
          display: "flex",
          flexWrap: "wrap",
          justifyContent: "space-between",
          outline: "1px solid black",
          padding: " 10px ",
          position: "sticky",
        }}
      >
        {" "}
        <p>
          Name:&nbsp;
          <b>{data.author}</b>
        </p>
        <p>
          Rating: <b>{data.author_details.rating}/10</b>
        </p>
      </div>

      <p style={{ margin: "10px" }}>{data.content}</p>
    </div>
  ));
};
const Review=() =>{
    
}
<div className="plot">
  <h4>
    <strong
      style={{
        borderRadius: "10px",
        backgroundColor: "#D8D9DA",
        padding: "0 5px",
      }}
    >
      {review?.length === 0 ? "No reviews" : "Top Reviews"}
    </strong>
  </h4>

  <div style={{ display: "flex", flexWrap: "wrap", marginTop: "10px" }}>
    {review?.length !== 0 && (
      <ScrollCarousel autoplay>{generateReview()}</ScrollCarousel>
    )}
  </div>
</div>;
