import React, { useEffect,useState}  from "react";
import movies from "../movies"
import { useParams } from "react-router-dom";
import Booking from "./Booking"

function Details(){
    const params=useParams()
    const userId=params.userId
    
    const obj=movies.find(function(num){
       
        return num.title===userId
    })
     const [movie,setmovieData]=useState([])
    const [found,setNotFound]=useState(null);

useEffect(() => {
    fetch(`http://www.omdbapi.com/?t=${obj.title}&y=${obj.year}&apikey=961ea94b`)
      .then((res) => res.json()
      ).then(data => setmovieData(data))
      
      .catch((err) => {
        setNotFound(true);
      })
      
  }, [])
      
    return (
        
        <>
        <div class="movie">
        <div class="abcd container px-4 ">
  <div class="row gx-5">
   <div class="col">
    <img src={movie.Poster} height="400" width="400" />
    </div>
  <div class="col">
    <h1>{movie.Title} </h1>
    <p> {movie.Genre} </p>
    <div></div>
    <p>{movie.imdbRating}/10</p>
    <div class="language">
      <h2>Cost</h2>
      <button type="button" class="btn btn-outline-info btn-sm" >200</button>
      <button type="button" class="btn btn-outline-primary" >150</button>
      <button type="button" class="btn btn-outline-primary" >100</button>
      
    </div>

       <div class="movie-container">
         <label>Select a Theatre</label>
         <select  id="movie">
           <option value="220">PVR</option>
           <option value="320">PVP</option>
           <option value="250">SP</option>
           <option value="230">CELLULOID</option>
         </select>
       </div>
      </div>

    </div>
  </div>
  <Booking />
  </div>
        </>
    )
}
export default Details;
