import React, { useState, useEffect } from "react";


const TypingAnimation = (props) => {
  var plot =
    props.data?.length > mvedata.overview?.length
      ? movie.Plot
      : mvedata.overview;
  const [text, setText] = useState(""); // Your 100-word response here
  const [visibleText, setVisibleText] = useState("");
  const [isTypingFinished, setIsTypingFinished] = useState(false);

  useEffect(() => {
    const words = text.split(" ");
    let index = 0;
    let interval;

    if (!isTypingFinished) {
      interval = setInterval(() => {
        setVisibleText(
          (prevVisibleText) => prevVisibleText + words[index] + " "
        );

        index++;
        if (index >= words.length) {
          setIsTypingFinished(true);
          clearInterval(interval);
        }
      }, 150); 

      return () => clearInterval(interval);
    }
  }, [text, isTypingFinished]);

  return (
    <div className="plotdata">
      <p>{visibleText}</p>
    </div>
  );
};

export default TypingAnimation;
