import React,{useEffect,useState} from "react";
import { Link } from "react-router-dom";

function Movie(props) {
   const [found, setNotFound] = useState(null);
   const [tmdata, setmdata] = useState([]);
   
   useEffect(() => {
     fetch(
       `https://api.themoviedb.org/3/search/movie?query=${props.title}&api_key=bee8ce9f0d5a33ee50837d31a61a64eb`
     )
       .then((res) => res.json())
       .then((data) => setmdata(data.results[0]))

       .catch((err) => {
         setNotFound(true);
       });
   }, []);


  return (
    <>
      <figure class="image-block">
        <h1>The Beach</h1>
        <img
          src="https://images.pexels.com/photos/1680140/pexels-photo-1680140.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=750&w=1260"
          alt=""
        />
        <figcaption>
          <h3>More Info</h3>
          <p>
            Pellentesque habitant morbi tristique senectus et netus et malesuada
            fames ac turpis egestas. Vestibulum tortor quam, feugiat vitae,
            ultricies eget, tempor sit amet, ante. Donec eu libero sit amet quam
            egestas semper. Aenean ultricies mi vitae est. Mauris placerat
            eleifend leo.
          </p>
          <button>More Info</button>
        </figcaption>
      </figure>
      <Link
        className="movie-card"
        state={{ name: props.name, data: tmdata, title: props.title }}
        to={`${tmdata.id}`}
      >
        <img src={props.poster} width="250" height="250" />
        <div className="card-text">
          <h3 className="card-title">{props.title}</h3>

          <p>{props.genre}</p>
        </div>
      </Link>
    </>
  );
}

export default Movie;
