import React, { useState } from "react";
import { useParams, Link, Outlet, useLocation } from "react-router-dom";

function Payment() {
  const location = useLocation();
  const cost = location.state.totalcost;
  const count = location.state.counts;
  const theater = location.state.theatre;
  const time = location.state.time;
  const date = location.state.date;
  const name = location.state.name;
  const title = location.state.title;
  const photo = location.state.photo;
  const [activeb, setactiveb] = useState(null);
  const [app, setApp] = useState(null);
  var num = cost / count;
  function handleselect(data) {
    setactiveb(data);
  }
  const  styles={
    backgroundImage : `https://image.tmdb.org/t/p/w500/` + photo
  }
  return (
    <div id="payment" style={{styles}}>
      <div className="payment">
        <div className="pay-photo">
          <img
            className="pay-image"
            src={`https://image.tmdb.org/t/p/w500/` + photo}
            height="120"
            width="120"
          />
        </div>
        <div className="pay-content">
          <p>
            <strong>{title}</strong>
          </p>
          <p>
            {date}- {time}
          </p>
        </div>
      </div>

      <div className="payment-cost">
        <div className="pay-cost"></div>
        <div className="pay-cost">
          <p>Cost of each ticket</p>
          <p>
            <i class="fa-solid fa-indian-rupee-sign"></i>
            {num}.00
          </p>
        </div>
        <div className="pay-cost">
          <p>No. of tickets</p>
          <p>{count}</p>
        </div>

        <div className="pay-cost">
          <p>Sub Total</p>

          <p>
            <i class="fa-solid fa-indian-rupee-sign"></i>
            {cost}.00
          </p>
        </div>
        <div className="pay-cost">
          <p>Commission Fee</p>
          <p>
            <span>Free</span>
          </p>
        </div>
        <hr />
        <div className="pay-cost">
          <p>Total Amount :</p>
          <p>
            <i class="fa-solid fa-indian-rupee-sign"></i> <span>{cost}.00</span>
          </p>
        </div>
        <hr />
        <div className="pay-cost">
          <button
            className="paycost-button"
            name="button1"
            value="google-pay"
            id={activeb === "button1" ? "payid" : "pay"}
            onClick={() => {
              handleselect("button1");
              setApp("Google-pay");
            }}
          >
            <i
              class="fa-brands fa-google-pay fa-2x"
              style={{ color: " #EA4335" }}
            ></i>
          </button>
          <button
            className="paycost-button"
            name="button1"
            value="amazon-pay"
            id={activeb === "button2" ? "payid" : "pay"}
            onClick={() => {
              handleselect("button2");
              setApp("Amazon-pay");
            }}
          >
            <i
              class="fa-brands fa-amazon-pay fa-2x"
              style={{ color: " #333E47" }}
            ></i>
          </button>
          <button
            className="paycost-button"
            name="button1"
            value="visa"
            id={activeb === "button3" ? "payid" : "pay"}
            onClick={() => {
              handleselect("button3");
              setApp("Visa");
            }}
          >
            <i
              class="fa-brands fa-cc-visa fa-2x"
              style={{ color: "#15195A" }}
            ></i>
          </button>
          <button
            className="paycost-button"
            name="button1"
            value="visa"
            id={activeb === "button4" ? "payid" : "pay"}
            onClick={() => {
              handleselect("button4");
              setApp("Apple-pay");
            }}
          >
            <i
              class="fa-brands fa-cc-apple-pay fa-2x"
              style={{ color: " black" }}
            ></i>
          </button>
        </div>
        {activeb && (
          <Link
            state={{
              totalcost: cost,
              counts: count,
              theatre: theater,
              time: time,
              date: date,
              name: name,
              title: title,
              photo: photo,
              payoption: app,
            }}
            to="success"
          >
            <div className="payconfirm">
              <button className="payconfirm-button">
                Pay <i class="fa-solid fa-indian-rupee-sign"></i> {cost}.00
              </button>
            </div>
          </Link>
        )}
      </div>
    </div>
  );
}

export default Payment;
