import React, { useEffect,useState} from "react";
import { Link } from "react-router-dom";

function Movie(props){
   const [movie,setmovieData]=useState(null)
    const [found,setNotFound]=useState(null);

useEffect(() => {
    fetch(`https://www.omdbapi.com/?t=${props.title}'&y=${props.year}&plot=full&apikey=f9c47ed2&'`)
      .then((res) => res.json())
      .then(([data]) => {
        setmovieData({
          title:data.Title
        })
      })
      .catch((err) => {
        setNotFound(true)
      })
  }, [])
   console.log(setmovieData.title);



   
  return(
    <>
    <Link className="country-card" to={`/${props.title}`}>
      <img src={props.poster} alt={props.title + ' Flag'} width="200" height="250"/>
      <div className="card-text">
        <h3 className="card-title">{props.title}</h3>
       
        <p>
          <b>Title: </b>{props.title}
        </p>
        <p>
          <b>genre: </b>{props.genre}
        </p>
      </div>
    </Link>
    

    </>
  )
}

export default Movie;
