import React,{ useEffect,useState } from "react";
import { useLocation } from "react-router-dom";

import movies from "../movies";
import Movie from "./Movie";

function Home() {
  const location = useLocation();
  const name = location.state.name;
  const mail = location.state.mail;
  const [count,setCount]=useState(false);
  
  var sortedmovies;
  function handleClick(){
    setCount(!count);
    console.log(count);
    if(count==1){
     sortedmovies = movies.sort((p1, p2) =>
       p1.title > p2.title ? 1 : p1.title < p2.title ? -1 : 0
     );
    
    }else{
      sortedmovies=null;
    }
        console.log(movies);
  }
  return (
    <>
      <div>
        <button onClick={handleClick}>
          <i class="fa-regular fa-bars-sort"></i>
        </button>
      </div>

      {count && sortedmovies && (
        <div className="moviesss-container">
          {sortedmovies.map((data) => (
            <Movie
              name={name}
              mail={mail}
              id={data.id}
              year={data.year}
              title={data.title}
              poster={data.poster}
              genre={data.genre}
            />
          ))}
        </div>
      )}
      {count === false && (
        <div className="moviesss-container">
          {movies.map((data) => (
            <Movie
              name={name}
              mail={mail}
              id={data.id}
              year={data.year}
              title={data.title}
              poster={data.poster}
              genre={data.genre}
            />
          ))}
        </div>
      )}
    </>
  );
}
export default Home;
