import React, { useEffect, useState } from "react";
import movies from "../movies";
import { useParams, Link, useLocation } from "react-router-dom";
import moment from "moment";
function Ticket() {
  const img_300 = "https://image.tmdb.org/t/p/w300";
  const noPicture =
    "https://upload.wikimedia.org/wikipedia/en/6/60/No_Picture.jpg";

  const params = useParams();
  const userId = params.userId;
  const location = useLocation();
  const name = location.state.name;
  const mvedata = location.state.data;
  const obj = movies.find(function (num) {
    return num.title === userId;
  });
  var days = [
    {
      id: 0,
      name: "button-1",
      date: moment().add(1, "days").format("ddd, MMM D"),
    },
    {
      id: 1,
      name: "button-2",
      date: moment().add(2, "days").format("ddd, MMM D"),
    },
    {
      id: 2,
      name: "button-3",
      date: moment().add(3, "days").format("ddd, MMM D"),
    },
    {
      id: 3,
      name: "button-4",
      date: moment().add(4, "days").format("ddd, MMM D"),
    },
  ];
  var theatres = [
    { name: "PVR" },
    { name: "PVP" },
    { name: "ASIAN" },
    { name: "INOX" },
    { name: "IMAX" },
  ];
  var costs = [{ cost: "200" }, { cost: "150" }, { cost: "100" }];

  const [movie, setmovieData] = useState([]);
  const [tmdata, setmdata] = useState([]);
  const [cost, setCost] = useState(null);
  const [found, setNotFound] = useState(null);
  const [credits, setCredits] = useState(null);
  const [crew, setCrew] = useState(null);
  const [activebutton, setactive] = useState(null);
  const [activeb, setactiveb] = useState(null);
  const [review, setReview] = useState(null);

  function handlecost(data) {
    setCost(data);
  }

  function dateclick(data) {
    setactive(data);
  }
  function theatreclick(data) {
    setactiveb(data);
  }

  useEffect(() => {
    fetch(
      `https://www.omdbapi.com/?t=${obj.title}&y=${obj.year}&apikey=961ea94b`
    )
      .then((res) => res.json())
      .then((data) => setmovieData(data))

      .catch((err) => {
        setNotFound(true);
      });
  }, []);

  useEffect(() => {
    fetch(
      `https://api.themoviedb.org/3/movie/${mvedata.id}/credits?api_key=bee8ce9f0d5a33ee50837d31a61a64eb`
    )
      .then((res) => res.json())
      .then((data) => setCredits(data.cast))

      .catch((err) => {
        setNotFound(true);
      });
  }, []);
   useEffect(() => {
     fetch(
       `https://api.themoviedb.org/3/movie/${mvedata.id}/reviews?api_key=bee8ce9f0d5a33ee50837d31a61a64eb`
     )
       .then((res) => res.json())
       .then((data) => setCrew(data))

       .catch((err) => {
         setNotFound(true);
       });
   }, []);
 useEffect(() => {
   fetch(
     `https://api.themoviedb.org/3/movie/${crew.id}/reviews?api_key=bee8ce9f0d5a33ee50837d31a61a64eb`
   )
     .then((res) => res.json())
     .then((data) => setReview(data.results))

     .catch((err) => {
       setNotFound(true);
     });
 }, []);
console.log(review);
  var credit = null;
  
  if (credits !== null) {
    credit = credits.slice(0, 6);
  }
 
  var num = 0;
  if (movie.imdbVotes !== undefined) {
    for (var i = 0; i < movie.imdbVotes.length; i++) {
      if (movie.imdbVotes[i] !== ",") {
        num += movie.imdbVotes[i];
      }
    }
  }
  var imdb =
    movie.imdbRating === "N/A" ? mvedata.vote_average : movie.imdbRating;

  var num1 = parseInt(num);
  num1 = num1 / 1000;
  num1 = Math.floor(num1);
  var t = parseInt(movie.Runtime);
  var h = Math.floor(t / 60);
  var min = Math.floor(t % 60);
  var vote = movie.imdbRating === "N/A" ? mvedata.vote_count : num1;
  return (
    <>
      <div class="movie">
        <div className="poster">
          <img
            className="movie-image"
            src={`https://image.tmdb.org/t/p/w500/` + mvedata.poster_path}
            height="400"
            width="400"
          />
        </div>

        <div className="matter">
          <h1 class="matter-title">{movie.Title} </h1>

          <div className="rating">
            <span className="imdb">
              <h2 class="abcd">
                <i
                  class="fa-sharp fa-solid fa-star"
                  style={{ color: "#f84464" }}
                ></i>
                {imdb}/10
              </h2>
            </span>
            <span>
              <strong>{vote}k</strong> votes
            </span>
          </div>

          <div className="elements">
            <ul>
              <li>
                {h}hr {min}min
              </li>
              <li>{movie.Genre}</li>
            </ul>
          </div>
          <div className="language">
            <li>{movie.Language}</li>
          </div>

          <div className="date">
            <label>
              <strong>Select Date </strong>
            </label>
            {days.map((data) => (
              <button
                type="button"
                name={data.date}
                id={activebutton === data.date ? "datebuttonid" : "datebutton"}
                onClick={() => dateclick(data.date)}
              >
                {data.date}
              </button>
            ))}
          </div>
          <div className="theatre">
            <label>
              <strong>SelectTheatre </strong>
            </label>
            {theatres.map((data) => (
              <button
                type="button"
                name={data.name}
                id={activeb === data.name ? "datebuttonid" : "datebutton"}
                onClick={() => theatreclick(data.name)}
              >
                {data.name}
              </button>
            ))}
          </div>
          {activebutton && activeb && (
            <div className="theatre">
              <label>
                <strong>SelectCost </strong>
              </label>
              {costs.map((data) => (
                <button
                  type="button"
                  name={data.cost}
                  id={cost === data.cost ? "datebuttonid" : "datebutton"}
                  onClick={() => handlecost(data.cost)}
                >
                  {data.cost}
                </button>
              ))}
            </div>
          )}
          {cost && activebutton && activeb && (
            <Link
              className="book-ticket"
              state={{
                data: activeb,
                cost: cost,
                date: activebutton,
                name: name,
              }}
              to={`${userId}`}
            >
              <button type="button" className="ticket-button">
                Book tickets
              </button>
            </Link>
          )}
        </div>
      </div>

      <div className="plot">
        <h4>About the Movie</h4>
        <b>{mvedata.overview}</b>
      </div>
      <div className="plot">
        <h4> Starring</h4>
        <div className="cast">
          {credit &&
            credit.map((c) => (
              <div>
                <div className="carouselItem">
                  <img
                    className="cast-img"
                    src={
                      c.profile_path
                        ? `${img_300}/${c.profile_path}`
                        : noPicture
                    }
                    alt={c?.name}
                    height="150"
                    width="150"
                  />
                </div>
                <div>
                  <b className="carouselItem__txt">{c?.name}</b>
                </div>
              </div>
            ))}
        </div>
        {!credit && <p>{movie.Actors}</p>}
      </div>
      <div className="plot">
        <h4>Directed by</h4>
        <b>{movie.Director}</b>
      </div>

      {review && <div>
        
      </div>}
    </>
  );
}

export default Ticket;
