import React,{useEffect,useState} from "react";
import { Link } from "react-router-dom";
import Nav from "./Nav";
function Movie(props) {
   const [found, setNotFound] = useState(null);
   const [tmdata, setmdata] = useState([]);
   useEffect(() => {
     fetch(
       `https://api.themoviedb.org/3/search/movie?query=${props.title}&api_key=bee8ce9f0d5a33ee50837d31a61a64eb`
     )
       .then((res) => res.json())
       .then((data) => setmdata(data.results[0]))

       .catch((err) => {
         setNotFound(true);
       });
   }, []);
   console.log(tmdata);
  return (
    <>
      <Link className="movie-card" state={{name:props.name, data:tmdata.id}} to={`${props.title}`}>
        <img
          src={props.poster}
          alt={props.title + " Flag"}
          width="250"
          height="250"
        />
        <div className="card-text">
          <h3 className="card-title">{props.title}</h3>

          <p>{props.genre}</p>
        </div>
      </Link>
    </>
  );
}

export default Movie;
