import React, { useEffect, useState } from "react";
import movies from "../movies";
import { useParams, Link, useNavigate } from "react-router-dom";
import moment from "moment";
function Ticket() {
  
  const params = useParams();
  const userId = params.userId;
  const history = useNavigate();
  const obj = movies.find(function (num) {
    return num.title === userId;
  });
  const [movie, setmovieData] = useState([]);
  const [cost, setCost] = useState(null);
  const [found, setNotFound] = useState(null);
  const [book, setBook] = useState(false);
  const [activebutton, setactive] = useState(null);
  function handleClick(data) {
    console.log(data);
  }
  const time = new Date().getHours();
  function handlemovie(data) {
    setCost(data.target.value);
  }
   var days = [
     {
       id: 0,
       name: "button-1",
       date: moment().add(0, "days").format("ddd, MMM D"),
     },
     {
       id: 1,
       name: "button-2",
       date: moment().add(1, "days").format("ddd, MMM D"),
     },
     {
       id: 2,
       name: "button-3",
       date: moment().add(2, "days").format("ddd, MMM D"),
     },
   ];
    function dateclick(data) {
      setactive(data);
    }
  function handleselect(data) {
    if (time >= data.target.value) {
      alert("Show has already started,Book for other show");
      setBook(false);
    } else {
      setBook(true);
    }
  }

  useEffect(() => {
    fetch(
      `http://www.omdbapi.com/?t=${obj.title}&y=${obj.year}&apikey=961ea94b`
    )
      .then((res) => res.json())
      .then((data) => setmovieData(data))

      .catch((err) => {
        setNotFound(true);
      });
  }, []);

  var num = 0;
  if (movie.imdbVotes !== undefined) {
    for (var i = 0; i < movie.imdbVotes.length; i++) {
      if (movie.imdbVotes[i] !== ",") {
        num += movie.imdbVotes[i];
      }
    }
  }

  var num1 = parseInt(num);
  num1 = num1 / 1000;
  num1 = Math.floor(num1);
  var t = parseInt(movie.Runtime);
  var h = Math.floor(t / 60);
  var min = Math.floor(t % 60);
  return (
    <>
      <div class="movie">
        <div className="poster">
          <img
            className="movie-image"
            src={movie.Poster}
            height="400"
            width="400"
          />
        </div>

        <div className="matter">
          <h1 class="matter-title">{movie.Title} </h1>

          <div className="rating">
            <span className="imdb">
              <h2 class="abcd">
                <i
                  class="fa-sharp fa-solid fa-star"
                  style={{ color: "rgb(248, 68, 100)" }}
                ></i>
                {movie.imdbRating}/10
              </h2>
            </span>
            <span>
              <strong>{num1}k</strong> votes
            </span>
          </div>

          <div className="elements">
            <ul>
              <li>
                {h}h {min}min
              </li>
              <li>{movie.Genre}</li>
            </ul>
          </div>
          <div className="language">
            <li>{movie.Language}</li>
          </div>

          <div className="movie-container">
            <label>
              <strong>Select a Theatre</strong>
            </label>
            <select id="movie" onChange={handlemovie}>
              <option value="">Select</option>
              <option value="PVR-220/-">PVR-220/-</option>
              <option value="PVP-320/-">PVP-320/-</option>
              <option value="ASIAN-250/-">ASIAN-250/-</option>
              <option value="INOX-230/-">INOX-230/-</option>
              <option value="IMAX-300/-">IMAX-300/-</option>
            </select>
          </div>
          <div className="date">
          <label></label>
            {days.map((data) => (
              <button
                type="button"
                name={data.name}
                id={activebutton === data.name ? "datebuttonid" : "datebutton"}
                onClick={() => dateclick(data.name)}
              >
                {data.date}
              </button>
            ))}
          </div>

          {cost && book && (
            <Link className="book-ticket" to={`${userId}`}>
              <button
                type="button"
                className="ticket-button"
                onClick={handleClick}
              >
                Boot tickets at {cost}
              </button>
            </Link>
          )}
        </div>
      </div>

      <div className="plot">
        <h4>About the Movie</h4>
        <p>{movie.Plot}</p>
      </div>
      <div className="plot">
        <h4> Starring</h4>
        <p>{movie.Actors}</p>
      </div>
      <div className="plot">
        <h4>Directed by</h4>
        <p>{movie.Director}</p>
      </div>
    </>
  );
}

export default Ticket;
