import React from "react";
import movies from "../movies"
import { useParams } from "react-router-dom";
import Booking from "./Booking"

function Details(){
    const params=useParams()
    const userId=params.userId
    const [movie,setMovieData]=useState(null)
    const obj=movies.find(function(num){
       
        return num.title===userId
    })
    
    useEffect(() => {
    fetch(`https://www.omdbapi.com/?t=${obj.title}'&y=${obj.year}&plot=full&apikey=f9c47ed2&'`)
      .then((res) => res.json())
      .then(([data]) => {
        setmovieData({
          title:data.Ti
        })
      })
      .catch((err) => {
        setNotFound(true)
      })
  }, [])

    return (
        
        <>
        <div class="movie">
        <div class="abcd container px-4 ">
  <div class="row gx-5">
   <div class="col">
    <img src={obj.poster} height="400" width="400" />
    </div>
  <div class="col">
    <h1>{obj.title} </h1>
    <p> {obj.genre} </p>
    <div class="language">
      <h2>Cost</h2>
      <button type="button" class="btn btn-outline-info btn-sm" >200</button>
      <button type="button" class="btn btn-outline-primary" >150</button>
      <button type="button" class="btn btn-outline-primary" >100</button>
      
    </div>

       <div class="movie-container">
         <label>Select a Theatre</label>
         <select  id="movie">
           <option value="220">PVR</option>
           <option value="320">PVP</option>
           <option value="250">SP</option>
           <option value="230">CELLULOID</option>
         </select>
       </div>
      </div>

    </div>
  </div>
  <Booking />
  </div>
        </>
    )
}
export default Details;
