import React, { useEffect,useState}  from "react";
import movies from "../movies"
import { useParams,Link,useNavigate } from "react-router-dom";
import Nav from "./Nav"


function Details(){
    const params=useParams()
    const userId=params.userId
    const history=useNavigate();
    const obj=movies.find(function(num){
      
        return num.title===userId
    })
     const [movie,setmovieData]=useState([])
     const [cost,setCost]=useState(0);
    const [found,setNotFound]=useState(null);
    const [book,setBook]=useState(false);
    function handleClick(data){
      console.log(data);
    }
  const time= new Date().getHours();
   function handlemovie(data) {
     setCost(data.target.value);
   }
  function handleselect(data){
    if(time>= data.target.value){
      alert("Show has already started,Book for other show");
      setBook(false)
    }
    else{
      setBook(true);
    }
  }

useEffect(() => {
    fetch(`http://www.omdbapi.com/?t=${obj.title}&y=${obj.year}&apikey=961ea94b`)
      .then((res) => res.json()
      ).then(data => setmovieData(data))
      
      .catch((err) => {
        setNotFound(true);
      })
      
  }, [])
     
    return (
      <>
        <Nav />
        <div class="movie">
          <div className="poster">
            <img
              className="movie-image"
              src={movie.Poster}
              height="400"
              width="400"
            />
          </div>

          <div className="matter">
            <h1 class="matter-title">{movie.Title} </h1>

            <div className="rating">
              <span className="imdb">
                <h2 class="abcd">
                  <i
                    class="fa-sharp fa-solid fa-star"
                    style={{ color: "rgb(248, 68, 100)" }}
                  ></i>
                  {movie.imdbRating}/10
                </h2>
              </span>
              <span> {movie.imdbVotes} </span>
              <span>votes</span>
            </div>

            <div className="elements"> 
              <ul>
                <li>{movie.Runtime}/60 h {movie.Runtime}%60 min </li>
                <li>{movie.Genre}</li>
              </ul>
            </div>
            <div className="language">
              <li>{movie.Language}</li>
            </div>

            <div className="movie-container">
              <label>Select a Theatre</label>
              <select id="movie" onChange={handlemovie}>
                <option value="220">PVR-220/-</option>
                <option value="320">PVP-320/-</option>
                <option value="250">ASIAN-250/-</option>
                <option value="230">INOX-230/-</option>
                <option value="300">IMAX-300/-</option>
              </select>
            </div>
            <div className="movie-container">
              <label>Select Time</label>
              <select id="movie" onChange={handleselect}>
                <option value="11">11:00 A.M</option>
                <option value="12">12:00 A.M</option>
                <option value="14">2:00 P.M</option>
                <option value="15">3:00 P.M</option>
                <option value="17">5:00 P.M</option>
                <option value="18">6:00 P.M</option>
                <option value="21">9:00 P.M</option>
                <option value="22">10:00 P.M</option>
              </select>
            </div>

            {book && (
              <Link className="book-ticket" to={`/booking/${movie.Title}`}>
                <button
                  type="button"
                  className="ticket-button"
                  onClick={handleClick}
                >
                  Boot tickets
                </button>
              </Link>
            )}
          </div>
        </div>

        <div className="plot">
          <h2>About the Movie</h2>
          <p> {movie.Plot}</p>
        </div>
      </>
    );
}
export default Details;
