import React from "react";
import cities from "../cities";
import { Link } from "react-router-dom";

export default function Ticket() {
  const [city, setCity] = React.useState("");
  function click(event) {
    setCity(event.target.value);
    console.log(city);
  }

  return (
    <div class="city-form">
      <h2>Select a city:</h2>

      {cities.map((data) => (
        <label class="rad-label" value={data.name} onClick={click}>
          <input type="radio" class="rad-input" name="rad" value={data.name} />
          <div class="rad-design"></div>
          <div class="rad-text">{data.name}</div>
        </label>
      ))}
      
    </div>
  );
}
