import React, { useEffect, useState } from "react";
import movies from "../movies";
import { useParams, Link, useLocation } from "react-router-dom";
import moment from "moment";
function Ticket() {
  const img_300 = "https://image.tmdb.org/t/p/w300";
  const noPicture =
    "https://upload.wikimedia.org/wikipedia/en/6/60/No_Picture.jpg";


  const location = useLocation();
  const name = location.state.name;
  const mvedata = location.state.data;
  const title = location.state.title;
  const obj = movies.find(function (num) {
    return num.title === title;
  });
  var days = [
    {
      id: 0,
      name: "button-1",
      date: moment().add(1, "days").format("ddd, MMM D"),
    },
    {
      id: 1,
      name: "button-2",
      date: moment().add(2, "days").format("ddd, MMM D"),
    },
    {
      id: 2,
      name: "button-3",
      date: moment().add(3, "days").format("ddd, MMM D"),
    },
    
  ];
  var theatres = [
    { name: "PVR" },
    { name: "PVP" },
    { name: "INOX" },
    { name: "IMAX" },
  ];
  var costs = [{ cost: "200" }, { cost: "150" }, { cost: "100" }];

  const [movie, setmovieData] = useState([]);
  const [tmdata, setmdata] = useState([]);
  const [cost, setCost] = useState(null);
  const [found, setNotFound] = useState(null);
  const [credits, setCredits] = useState(null);
  const [crew, setCrew] = useState(null);
  const [activebutton, setactive] = useState(null);
  const [activeb, setactiveb] = useState(null);
  const [trailer, settrailer] = useState(null);

  function handlecost(data) {
    setCost(data);
  }

  function dateclick(data) {
    setactive(data);
  }
  function theatreclick(data) {
    setactiveb(data);
  }

  useEffect(() => {
    fetch(
      `https://www.omdbapi.com/?t=${obj.title}&y=${obj.year}&apikey=961ea94b`
    )
      .then((res) => res.json())
      .then((data) => setmovieData(data))

      .catch((err) => {
        setNotFound(true);
      });
  }, []);

  useEffect(() => {
    fetch(
      `https://api.themoviedb.org/3/movie/${mvedata.id}/credits?api_key=bee8ce9f0d5a33ee50837d31a61a64eb`
    )
      .then((res) => res.json())
      .then((data) => setCredits(data.cast))

      .catch((err) => {
        setNotFound(true);
      });
  }, []);

  var credit = null;

  if (credits !== null) {
    credit = credits.slice(0, 6);
  }

  var num = 0;
  if (movie.imdbVotes !== undefined) {
    for (var i = 0; i < movie.imdbVotes.length; i++) {
      if (movie.imdbVotes[i] !== ",") {
        num += movie.imdbVotes[i];
      }
    }
  }var num3 = parseInt(mvedata.vote_average);
  console.log(num3)
  var imdb =
    movie.imdbRating === "N/A" ?num3 : movie.imdbRating;

  var num1 = parseInt(num);
  num1 = num1 / 1000;
  num1 = Math.floor(num1);
  var t = parseInt(movie.Runtime);
  var h = Math.floor(t / 60);
  var min = Math.floor(t % 60);
  
  var vote = movie.imdbRating === "N/A" ? mvedata.vote_count : num1;
  return (
    <>
      <div className="movie">
        {" "}
       
         <div class="card__frame">
        <div class="card__picture">
          <img  src={`https://image.tmdb.org/t/p/w500/` + mvedata.poster_path} width="400" height="400" />
        </div>
        <h2 class="card__title">Design</h2>
      </div>
      <div class="card__overlay"></div>
      <div class="card__content">
        <h2>Design</h2>
        <p>Lorem, ipsum dolor sit amet consectetur adipisicing elit. Neque ipsum veritatis id quia cupiditate sed architecto aliquam nostrum unde nam minima voluptas dicta, beatae sint reprehenderit sit ducimus officiis ratione?</p>
      </div>
        <div className="matter">
          <h1 className="matter-title">{movie.Title} </h1>

          <div className="rating">
            <span className="imdb">
              <h2 className="abcd">
                <i
                  className="fa-sharp fa-solid fa-star"
                  style={{ color: "#f84464" }}
                ></i>
                {imdb}/10
              </h2>
            </span>
            <span>
              <strong>{vote}k</strong> votes
            </span>
          </div>

          <div className="elements">
            <ul>
              <li>
                {h}hr {min}min
              </li>
              <li>{movie.Genre}</li>
            </ul>
          </div>
          <div className="language">
            <li>{movie.Language}</li>
          </div>

          <div className="date">
            <label>
              <strong>Select Date </strong>
            </label>
            {days.map((data) => (
              <button
                type="button"
                name={data.date}
                id={activebutton === data.date ? "datebuttonid" : "datebutton"}
                onClick={() => dateclick(data.date)}
              >
                {data.date}
              </button>
            ))}
          </div>
          <div className="theatre">
            <label>
              <strong>SelectTheatre </strong>
            </label>
            {theatres.map((data) => (
              <button
                type="button"
                name={data.name}
                id={activeb === data.name ? "datebuttonid" : "datebutton"}
                onClick={() => theatreclick(data.name)}
              >
                {data.name}
              </button>
            ))}
          </div>
          {activebutton && activeb && (
            <div className="theatre">
              <label>
                <strong>SelectCost </strong>
              </label>
              {costs.map((data) => (
                <button
                  type="button"
                  name={data.cost}
                  id={cost === data.cost ? "datebuttonid" : "datebutton"}
                  onClick={() => handlecost(data.cost)}
                >
                  {data.cost}
                </button>
              ))}
            </div>
          )}
          {cost && activebutton && activeb && (
            <Link
              className="book-ticket"
              state={{
                data: activeb,
                cost: cost,
                date: activebutton,
                name: name,
                photo: mvedata.poster_path,
                title: title,
              }}
              to="booking"
            >
              <button type="button" className="ticket-button">
                Book tickets
              </button>
            </Link>
          )}
        </div>
      </div>

      <div className="plot">
        <h4>
          <strong>About the Movie</strong>
        </h4>
        <p>{mvedata.overview}</p>
      </div>
      <div className="plot">
        <h4>
          <strong>Starring</strong>{" "}
        </h4>
        <div className="cast">
          {credit &&
            credit.map((c) => (
              <div>
                <div className="carouselItem">
                  <img
                    className="cast-img"
                    src={
                      c.profile_path
                        ? `${img_300}/${c.profile_path}`
                        : noPicture
                    }
                    alt={c?.name}
                    height="150"
                    width="150"
                  />
                </div>
                <div>
                  <p className="carouselItem__txt">{c?.name}</p>
                </div>
              </div>
            ))}
        </div>
        {!credit && <p>{movie.Actors}</p>}
      </div>
      <div className="plot">
        <h4>
          <strong>Directed by</strong>
        </h4>
        <b>{movie.Director}</b>
      </div>
    </>
  );
}

export default Ticket;
