import React,{useEffect,useState} from "react";
import { Link } from "react-router-dom";

function Movie(props) {
   const [found, setNotFound] = useState(null);
   const [tmdata, setmdata] = useState([]);
   const [crew,setCrew]=useState(null);
   useEffect(() => {
     fetch(
       `https://api.themoviedb.org/3/search/movie?query=${props.title}&api_key=bee8ce9f0d5a33ee50837d31a61a64eb`
     )
       .then((res) => res.json())
       .then((data) => setmdata(data.results[0]))

       .catch((err) => {
         setNotFound(true);
       });
   }, []);
   console.log(tmdata);
   useEffect(() => {
     fetch(
       `https://api.themoviedb.org/3/movie/${tmdata.id}/videos?api_key=bee8ce9f0d5a33ee50837d31a61a64eb`
     )
       .then((res) => res.json())
       .then((data) => setCrew(data.results[0]))

       .catch((err) => {
         setNotFound(true);
       });
   }, []);
   console.log(crew);
  return (
    <>
      <Link state={{ name: props.name, data: tmdata }} to={`${props.title}`}>
        <div className="movie-card">
          <img
            src={props.poster}
            alt={props.title + " Flag"}
            width="250"
            height="250"
          />{" "}
        </div>
      </Link>

      <div className="card-text">
        <h3 className="card-title">{props.title}</h3>

        <p>{props.genre}</p>
      </div>
    </>
  );
}

export default Movie;
