import React, { useState } from "react";
import { Link, useParams, Outlet, useLocation } from "react-router-dom";

export default function Nav() {
 const [toggle, setToggle] = useState(false);
  const params = useParams();
  const city = params.cityname;
  const location= useLocation();
 const name=location.state.name;
  return (
    <>
      <nav className="navbar navbar-toggleable-xl navbar-inverse navbar-expand-lg navbar-dark ">
        <div>
          <a href="/">FlixBooking</a>
        </div>

          <div className="d-flex align-items-center">
            <div className="data-container">
              <i className="fa-solid fa-location-dot "></i> {city}
            </div>
            <div>
              <img
                src="	https://media.geeksforgeeks.org/img-practice/user_web-1598433228.svg"
                className="rounded-circle"
                height="30"
                alt="Black and White Portrait of a Man"
                loading="lazy"
              />
            </div>
          </div>
      
      </nav>

      <Outlet />
    </>
  );
}
